import React, { useState } from 'react'
import './MainPage.css'

function MainPage() {
	const [gameIsStarted, setGameIsStarted] = useState(false)
	const [text, setText] = useState()

	const [players, setPlayers] = useState([
		{ id: 1213231231, name: 'Egor', role: '' },
		{ id: 121999931231, name: 'Yarik', role: '' },
	])
	const addHandler = () => {
		setPlayers([...players, { id: Math.random(), name: text, role: '' }])
		setText('')
	}
	const deleteHandler = idPl => {
		setPlayers(players.filter(pl => pl.id !== +idPl.target.id))
	}

	const startGame = () => {
		let m = 0
		let d = 0
		let sh = 0
		setPlayers(
			players.map(player => {
				let r = Math.ceil(Math.random() * 4)
				console.log(r)
				if (r === 1 && m === 0) {
					player.role = 'Мафия'
					m++
				}
				if (r === 2 && d === 0) {
					player.role = 'Дон'
					d++
				}
				if (r === 3 && sh === 0) {
					player.role = 'Шериф'
					sh++
				}
				if (r === 4) {
					player.role = 'Мирный житель'
				}
				return player
			})
		)
		setGameIsStarted(true)
	}
	return (
		<>
			<div className={`${gameIsStarted ? 'noDisplay' : ''}`}>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала идите нахуй</h4>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index + 1} игрок
								</span>
								<button
									className='delete'
									id={player.id}
									onClick={deleteHandler}
								>
									x
								</button>
							</li>
						)
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
					onKeyDown={e => {
						if (e.key === 'Enter') {
							addHandler()
						}
					}}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
				<div>
					<button
						className={`enter ${players.length < 4 ? 'noDisplay' : ''}`}
						onClick={startGame}
					>
						Начать игру
					</button>
				</div>
			</div>
			<div className={`${gameIsStarted ? '' : 'noDisplay'}`}>
				<h1>Игра началась</h1>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index + 1} игрок с ролью {player.role}
								</span>
								<button
									className='delete'
									id={player.id}
									onClick={deleteHandler}
								>
									x
								</button>
							</li>
						)
					})}
				</ul>
			</div>
			{/* <Routes>
				<Route path='/home' element={<Home />}></Route>
			</Routes> */}
		</>
	)
}

export default MainPage
