import React, { useState } from 'react'
import './MainPage.css'
function MainPage() {
	const [text, setText] = useState()

	const [players, setPlayers] = useState([
		{ id: 1213231231, name: 'Egor' },
		{ id: 121999931231, name: 'Yarik' },
	])
	const addHandler = () => {
		setPlayers([...players, { id: Math.random(), name: text }])
		setText('')
	}
	const deleteHandler = idPl => {
		console.log(idPl.target.id)
		console.log(players.findIndex(pl => pl.id === idPl.target.id))
		setPlayers(
			players.slice(
				players.findIndex(pl => pl.id === idPl.target.id),
				1
			)
		)
	}
	return (
		<>
			<div>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала идите нахуй</h4>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index + 1} игрок
								</span>
								<button
									className='delete'
									id={player.id}
									onClick={deleteHandler}
								>
									x
								</button>
							</li>
						)
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
					onKeyDown={e => {
						if (e.key === 'Enter') {
							addHandler()
						}
					}}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
			</div>
			{/* <Routes>
				<Route path='/enter' element={<Enter />}></Route>
				<Route path='/np' element={<NewPage />}></Route>
			</Routes> */}
		</>
	)
}

export default MainPage
