import React, { useState } from 'react'
import './MainPage.css'
function MainPage() {
	const [text, setText] = useState()
	const [players, setPlayers] = useState([])
	const addHandler = () => {
		setPlayers([...players, { id: Math.random(), name: text }])
		setText('')
	}
	return (
		<>
			<div>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала идите нахуй</h4>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index} игрок
								</span>
								<button className='delete'>x</button>
							</li>
						)
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
					onKeyDown={e => {
						if (e.key === 'Enter') {
							addHandler()
						}
					}}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
			</div>
			{/* <Routes>
				<Route path='/enter' element={<Enter />}></Route>
				<Route path='/np' element={<NewPage />}></Route>
			</Routes> */}
		</>
	)
}

export default MainPage
