import React, { useState } from 'react'
import './MainPage.css'
function MainPage() {
	const [players, setPlayers] = useState([])

	const addPlayer = playerName => {
		setPlayers([...players, { name: playerName, role: 'civilian' }])
	}

	return (
		<div className={'div'}>
			<h1>Добро пожаловать в "Мафия"</h1>
			<h4>Для начала зарегистрируй аккаунт</h4>
			<ul className={'ul'}>
				{players.map((player, index) => {
					return <li key={index}>{player.name}</li>
				})}
			</ul>
			<input type='text' id='playerName' className={'input'} />
		</div>
	)
}

export default MainPage
