import React, { useState } from 'react'
import { Routes } from 'react-router-dom'
import './MainPage.css'
function MainPage() {
	const [text, setText] = useState()
	const [players, setPlayers] = useState([])
	const addHandler = playerName => {
		setPlayers([...players, { name: playerName, role: 'civilian' }])
	}

	return (
		<>
			<div>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала идите нахуй</h4>
				<ul className={'ul'}>
					{players.map((player, index) => {
						return <li key={index}>{player.name}</li>
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
			</div>
			<Routes>
				{/* <Route path='/enter' element={<Enter />}></Route>
				<Route path='/np' element={<NewPage />}></Route> */}
			</Routes>
		</>
	)
}

export default MainPage
