import React, { useState } from 'react'
import './MainPage.css'

function MainPage() {
	const [gameIsStarted, setGameIsStarted] = useState(false)
	const [text, setText] = useState()

	const [players, setPlayers] = useState([
		{ id: 1213231231, name: 'Egor' },
		{ id: 121999931231, name: 'Yarik' },
	])
	const addHandler = () => {
		setPlayers([...players, { id: Math.random(), name: text }])
		setText('')
	}
	const deleteHandler = idPl => {
		setPlayers(players.filter(pl => pl.id !== +idPl.target.id))
	}

	return (
		<>
			<div>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала идите нахуй</h4>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index + 1} игрок
								</span>
								<button
									className='delete'
									id={player.id}
									onClick={deleteHandler}
								>
									x
								</button>
							</li>
						)
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
					onKeyDown={e => {
						if (e.key === 'Enter') {
							addHandler()
						}
					}}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
				<div>
					<button
						className={`enter ${players.length < 4 ? 'noDisplay' : ''}`}
						onClick={(gameIsStarted = true)}
					>
						Начать игру
					</button>
				</div>
			</div>
			{/* <Routes>
				<Route path='/home' element={<Home />}></Route>
			</Routes> */}
		</>
	)
}

export default MainPage
