import React, { useState } from 'react'
import { Link, Route, Router } from 'react-router-dom'
import './MainPage.css'
function Enter() {
	const [players, setPlayers] = useState([])

	const addPlayer = playerName => {
		setPlayers([...players, { name: playerName, role: 'civilian' }])
	}

	return (
		<Router>
			<div>
				<h1>Добро пожаловать в "Вход"</h1>
				<h4>Для начала зарегистрируй аккаунт</h4>
				<button className={'enter'}>вход</button>
				<Link to={'/'}>egor </Link>
				<button className={'enter'}>регистрация</button>
				<ul className={'ul'}>
					{players.map((player, index) => {
						return <li key={index}>{player.name}</li>
					})}
				</ul>
			</div>
			<switch>
				<Route path='/'></Route>
			</switch>
		</Router>
	)
}

export default Enter
