import React, { useState } from 'react'
import './MainPage.css'

function MainPage() {
	const [gameIsStarted, setGameIsStarted] = useState(false)
	const [text, setText] = useState()

	const [players, setPlayers] = useState([
		{ id: 1213, name: 'Egor', role: '' },
		{ id: 1219, name: 'Yarik', role: '' },
		{ id: 1222, name: 'stepa', role: '' },
		{ id: 1224, name: 'dima', role: '' },
		{ id: 1225, name: 'vlad', role: '' },
		{ id: 1228, name: 'nikita', role: '' },
		{ id: 1229, name: 'ars', role: '' },
	])

	const addHandler = () => {
		setPlayers([...players, { id: Date.now(), name: text, role: '' }])
		setText('')
	}
	const deleteHandler = idPl => {
		setPlayers(players.filter(pl => pl.id !== +idPl.target.id))
	}

	const [countInTimeout, setCountInTimeout] = useState(0)
	const [countInInterval, setCountInInterval] = useState(6)
	const timer = () => {
		const countInterval = setInterval(() => {
			setCountInInterval(prevCount => prevCount - 1)
		}, 1000)

		if (countInInterval === 0) {
			clearInterval(countInterval)
		}
	}

	const startGame = () => {
		let r = 1
		setPlayers(
			players
				.toSorted(() => Math.random() - 0.5)
				.map(player => {
					if (r === 1) {
						player.role = 'Мафия'
					}
					if (r === 2) {
						player.role = 'Дон'
					}
					if (r === 3) {
						player.role = 'Шериф'
					}
					if (r >= 4) {
						player.role = 'Мирный житель'
					}
					r++
					return player
				})
				.toSorted((a, b) => {
					return a.id - b.id
				})
		)
		setGameIsStarted(true)
		timer()
	}

	return (
		<>
			<div className={`${gameIsStarted ? 'noDisplay' : ''}`}>
				<h1>Добро пожаловать в "Мафия"</h1>
				<h4>Для начала введите всех игроков</h4>
				<ul>
					{players.map((player, index) => {
						return (
							<li key={index}>
								<span>
									{player.name} - {index + 1} игрок
								</span>
								<button
									className='delete'
									id={player.id}
									onClick={deleteHandler}
								>
									x
								</button>
							</li>
						)
					})}
				</ul>
				<input
					value={text}
					placeholder='Имя'
					onChange={e => setText(e.target.value)}
					onKeyDown={e => {
						if (e.key === 'Enter') {
							addHandler()
						}
					}}
				></input>
				<button className={'enter'} onClick={addHandler}>
					Ввести имя
				</button>
				<div>
					<button
						className={`enter ${players.length < 4 ? 'noDisplay' : ''}`}
						onClick={startGame}
					>
						Начать игру
					</button>
				</div>
			</div>
			{/* здесь по своей сути начинается  компонент самой игры но мне пока лень разбивать это на компоненты и мучатся с пропсами*/}
			<div className={`${gameIsStarted ? '' : 'noDisplay'}`}>
				<h1>Игра началась</h1>
				<div>Оставшееся время для защиты {countInInterval}</div>
			</div>
			{/* <Routes>
				<Route path='/home' element={<Home />}></Route>
			</Routes> */}
		</>
	)
}

export default MainPage
