import React, { useState } from 'react'
import styles from './MainPage'
// Основной компонент игры "Мафия"
function MainPage() {
	const [players, setPlayers] = useState([]) // Состояние всех игроков

	// Функция добавления нового игрока
	const addPlayer = playerName => {
		setPlayers([...players, { name: playerName, role: 'civilian' }])
	}

	// Функция начала игры
	const startGame = () => {
		// Логика начала игры, распределение ролей и т.д.
	}

	// Отображение списка игроков и кнопки для добавления игрока
	return (
		<div className={styles.div}>
			<h1>Игра "Мафия"</h1>
			<ul>
				{players.map((player, index) => {
					return <li key={index}>{player.name}</li>
				})}
			</ul>
			<input type='text' id='playerName' />
			<button
				onClick={() => addPlayer(document.getElementById('playerName').value)}
			>
				Добавить игрока
			</button>
			<button onClick={startGame}>Начать игру</button>
		</div>
	)
}

export default MainPage
